    centerY: api.frameHeight / 2,
    radius: api.frameWidth / 6,
    bgColor: &#39;#87c0ed&#39;,
    pgColor: &#39;#325570&#39;,
    fixedOn: api.frameName
}, function(ret, err) {
    setValue({
        id: ret.id,
        value: 0
    })
});

function setValue(obj) {
    if (obj.value == 100) {
        obj.value = 0;
    }
    setTimeout(function() {
        arcProgress.setValue({
            id: obj.id,
            value: ++obj.value
        });
        setValue(obj)
    }, 100);
}

