            lon: 111.57062599999995,    // 经度
            lat: 33.784214              // 纬度
        },
        title: &#34;龙蛇之窟&#34;,               // 描述信息
        address: &#34;鸡冠洞&#34;               // 地址信息，与position配合为空
    }
}, function(ret, err) {
    if (ret.status) {
        api.alert({
            title: &#34;提示&#34;,
            msg: &#39;导航成功&#39;
        });
    } else {
        var msg = &#34;未知错误&#34;;
        if (1 == err.code) {
            msg = &#34;获取地理位置失败&#34;;
        }
        if (2 == err.code) {
            msg = &#34;定位服务未开启&#34;;
        }
        if (3 == err.code) {
            msg = &#34;线路取消&#34;;
        }
        if (4 == err.code) {
            msg = &#34;退出导航&#34;;
        }
        if (5 == err.code) {
            msg = &#34;退出导航声明页面&#34;;
        }
        api.alert({
