//启动打开监听区域回调通道，使已监听区域有效
apiready = function() {
    bbsdk = api.require(&#39;brightBeacon&#39;);
    //所有区域监听回调都会是该回调出口
    bbsdk.startMonitoring(function(ret, err) {
        if (ret.status) {
            if (ret.state) {
                var param = { &#34;msg&#34;: (ret.state == 1 ? &#34;进入区域通知&#34; : &#34;离开区域通知&#34;), &#34;action&#34;: &#34;立即查看&#34;, &#34;userInfo&#34;: &#34;自定义传输参数&#34; };
                bbsdk.sendLocalNotification(param, function(ret1, err1) {
                    if (ret1.status) {
                        //alert(JSON.stringify(ret1));
                    } else {
                        alert(err1.error);
                    }
                });
            }
        } else {
            alert(err.error);
        }
    });
};


//再次开启监听区域，但区域监听只会继续使用初次打开的通道出口。
var bbsdk = api.require(&#34;brightBeacon&#34;);
bbsdk.startMonitoring({
    uuid: &#34;E2C56DB5-DFFB-48D2-B060-D0F5A71096E0&#34;,
    mac: &#34;xx:xx:xx:xx:xx:xx&#34;
    major: 1,
    minor: 1， in : 1, //ios进入区域检测
    out: 1, //ios离开区域检测
    display: 1 //ios点亮屏幕区域检测
}, function(ret, err) {
    if (!ret.status) alert(err.error);
});

var bbsdk = api.require(&#34;brightBeacon&#34;);
bbsdk.stopMonitoring({
    uuid: &#34;E2C56DB5-DFFB-48D2-B060-D0F5A71096E0&#34;,
    mac: &#34;78:A5:6B:12:7B&#34;
    major: 1,
    minor: 1
}, function(ret, err) {});

var bbsdk = api.require(&#34;brightBeacon&#34;);
bbsdk.sendLocalNotification({
    &#34;msg&#34;: &#34;区域通知&#34;,
    &#34;action&#34;: &#34;立即查看&#34;,
    &#34;userInfo&#34;: &#34;自定义传输参数&#34;
}, function(ret, err) {
    if (ret.status) {
        alert(JSON.stringify(ret1));
    } else {
        alert(err.error);
    }
});

var bbsdk = api.require(&#34;brightBeacon&#34;);
bbsdk.startAdvertising({
    &#34;uuid&#34;: &#34;E2C56DB5-DFFB-48D2-B060-D0F5A71096E0&#34;,
    &#34;major&#34;: 65535,
    &#34;minor&#34;: 65535,
    &#34;mac&#34;: &#34;&#34;,
    &#34;identifier&#34;: &#34;demo&#34;
}, function(ret, err) {
    if (ret.status) {
        alert(&#34;调用模拟Beacon成功&#34;);
    } else {
        alert(err.error);
    }
});

