var geeTest = api.require(&#39;geeTest&#39;);
geeTest.openGTView({
    api_1: &#39;http://webapi.geetest.com/apis/start-mobile-captcha/&#39;,
    api_2: &#39;http://webapi.geetest.com/apis/mobile-server-validate/&#39;,
    blurType: &#39;dark&#39;,
    https: &#39;false&#39;
}, function(ret, err) {
    if (ret.status) {
        /* Todo Your Code */
        api.alert({
            title: &#39;验证结果&#39;,
            msg: &#39;验证成功&#39;,
            buttons: [&#39;确定&#39;]
        });

    } else {

        if (err.msg == &#39;-100&#39;) {
            api.alert({
                title: &#39;验证信息错误&#39;,
                msg: &#39;Invalid config params. Check Your Server/Client Config.&#39;,
                buttons: [&#39;确定&#39;]
            });
        } else if (err.msg == &#39;-200&#39;) {
            api.alert({
                title: &#39;验证不可用&#39;,
                msg: &#39;极验服务不可用, 请使用备用验证&#39;,
                buttons: [&#39;确定&#39;]
            });
        } else if (err.msg == &#39;-300&#39;) {
            api.alert({
                title: &#39;验证关闭&#39;,
                msg: &#39;用户关闭了验证&#39;,
                buttons: [&#39;确定&#39;]
            });
        } else if (err.msg == &#39;-400&#39;) {
            api.alert({
                title: &#39;验证结果&#39;,
                msg: &#39;二次验证失败&#39;,
                buttons: [&#39;确定&#39;]
            });
        } else {
            api.alert({
                title: &#39;验证异常&#39;,
                msg: err.msg,
                buttons: [&#39;确定&#39;]
            });
        }
    }
});

