var recCard = api.require(&#39;hanvonCloudBcardReader&#39;);
api.getPicture({
    sourceType : &#39;library&#39;,
    encodingType : &#39;jpg&#39;,
    mediaValue : &#39;pic&#39;,
    destinationType : &#39;url&#39;,
    allowEdit : true,
    quality : 100,                
    saveToPhotoAlbum : false
}, function(ret, err) {
    if (ret) {
        url = ret.data;
        //console.log(url);
        recCard.recognitionBcard({
            androidkey : &#34;163114c8-31b5-4424-bb85-617f81cf54d9&#34;,
            iOSkey: &#34;163114c8-31b5-4424-bb85-617f81cf54d9&#34;,
            lang : &#34;chns&#34;,
            picpath : url
        }, function(ret, err) {
            if (ret.status) {
                api.alert({
                    title : &#34;识别结果&#34;,
                    msg : ret.result
                });
            } else {
                api.alert({
                    title : &#34;识别结果&#34;,
                    msg : err.msg
                });
            }
        });
    } else {
        alert(JSON.stringify(err));
    }
});

