    alert(&#34;收到通知被展示的回调，title：&#34; + ret.title + &#34;，content：&#34; + ret.content + &#34;，customContent：&#34; + ret.customContent + &#34;,activity:&#34; + ret.activity + &#34;,actionType:&#34; + ret.actionType + &#34;,msgid:&#34; + ret.msgid);
};
