// 点击界面中的返回按钮(图标)时结束自定义页面统计
function closeWin() {
    onPageEnd();
    api.closeWin();
}

function onPageEnd() {
    umengAnalytics.onPageEnd({
        pageName: &#39;列表页&#39;,
    }, function(ret, err) {
        if (ret.status) {
            //api.alert({msg: &#39;结束统计自定义页面&#39;});
        } else {
            api.alert({ msg: err.code + &#39; &#39; + err.msg });
        }
    });
}
apiready = function() {
    umengAnalytics = api.require(&#39;umengAnalytics&#39;);
    // ...此处省略 onPageStart 的代码....
    // iOS手势关闭页面时结束自定义页面统计
    api.addEventListener({
        name: &#39;viewdisappear&#39;
    }, function(ret, err) {
        onPageEnd()
    });
    // Android按返回键关闭页面时结束自定义页面统计
    api.addEventListener({
        name: &#39;keyback&#39;
    }, function(ret, err) {
        closeWin();
    });
};

var umengAnalytics = null;
apiready = function() {
        umengAnalytics = api.require(&#39;umengAnalytics&#39;);
    }
    // 计数事件1：统计发生次数（只有 eventId 一个参数时）
    // 例：统计微博转发次数
function forward() {
    umengAnalytics.onEvent({
        eventId: &#39;Forward&#39;,
    }, function(ret, err) {
        if (ret.status) {
            alert(JSON.stringify(ret));
        } else {
            alert(JSON.stringify(err));
        }
    });
}
// 计数事件2：统计点击行为各属性被触发的次数（有 eventId、attributes 两个参数时）
// 例：统计购买商品类型为book，数量为3本
function purchase() {
    umengAnalytics.onEvent({
        eventId: &#39;Purchase&#39;,
        attributes: {
            type: &#39;book&#39;,
            quantity: &#39;3&#39;
        }
    }, function(ret, err) {
        if (ret.status) {
            alert(JSON.stringify(ret));
        } else {
            alert(JSON.stringify(err));
        }
    });
}
// 计算事件：统计数值型变量的值的分布（有 eventId、attributes、 counter 三个参数时）
// 统计书名为《APICloud入门》，付款金额为52元
function pay() {
    umengAnalytics.onEvent({
        eventId: &#39;Pay&#39;,
        attributes: {
            book: &#39;APICloud入门&#39;
        },
        counter: 52,
    }, function(ret, err) {
        if (ret.status) {
            alert(JSON.stringify(ret));
        } else {
            alert(JSON.stringify(err));
        }
    });
}

